#pragma once

#include "CoreMinimal.h"
#include "Styling/SlateStyle.h"

/**
 * 
 */
class MenuItemIconStyle
{
public:
    
    /*
    Logic for unregistering all style sets during shutdown of the plugin.
    */
    static void Shutdown();

    /*
    Registers an icon with Slate to be used with toolbar buttons and menu items.
    @param PluginName Name of the plugin folder under which the icon asset can be found (e.g. USDImporter).
    @param IconPath Relative path to the icon asset within the plugin directory (e.g. Resources/ButtonIcon_40x.png).
    */
    static void RegisterIcon(const FString& PluginName, const FString& IconPath);

private:
    static TSharedRef< class FSlateStyleSet > Create(const FString& PluginName, const FString& IconPath);

    static TMap<FString, TSharedRef< FSlateStyleSet >> RegisteredStyleSets;
};